import React, { Component } from 'react';


export class InProzent extends Component {
    constructor(props) {
      super(props);
      this.state = {
          A: 100,
          B: 20,
          inProzent: 20,
          einEuro: 1,
          cssClass: "ShowOutput",
          swap: false
        };

      this.handleChangeA = this.handleChangeA.bind(this);
      this.handleChangeB = this.handleChangeB.bind(this);
      this.inProzent = this.inProzent.bind(this);
    }

    handleChangeA(event) {
      this.setState({cssClass: ""});

       this.setState({A: Number( event.target.value)}, this.inProzent);
    }
    handleChangeB(event) {

      this.setState({B: Number( event.target.value)}, this.inProzent);
    }

    inProzent(){

      const W = this.state.B;
      const G = this.state.A;
      this.setState({inProzent: W/G*100 });
      this.setState({einEuro: G*1 /100 });

        this.setState({cssClass: ""});  
        this.setState({swap: 0});    

    }

    render() {
      this.setState({swap: this.state.swap++});    

      if(this.state.swap === 10)
      {
        this.setState({cssClass: "ShowOutput"});  
         this.setState({swap: false}); 

      }
      return (
        <div onSubmit={this.handleSubmit}>
          <label>
            Name:
            <div>
            <p>Komplet(€) Z.B: 100€</p>
            A:<input type="number" value={this.state.A} onChange={this.handleChangeA} /> (€)
              </div>
              <div>
                <p>Teil(€) Z.B: 20€</p>              
               B:<input  nput type="number" value={this.state.B} onChange={this.handleChangeB} /> (€)

              </div>
          </label>
        <button onClick={this.inProzent}>Run</button>
        <div>
        Output:
       <p>Teil in Prozent  Z.B: 20%: </p>
       <p className={this.state.cssClass}> Teil(€) {this.state.B } = {this.state.inProzent}%</p> 
       <p>1% ist Z.B: 1€: </p>
       <p className={this.state.cssClass}>1% = {this.state.einEuro}€</p>

        </div>
        </div>
      );
    }
  }
  export default InProzent;
