import React, { Component } from 'react';


export class Rabattrechner extends Component {
    constructor(props) {
      super(props);
      this.state = {
          A: 0,
          B: 0,
          gespart: 0
        };

      this.handleChangeA = this.handleChangeA.bind(this);
      this.handleChangeB = this.handleChangeB.bind(this);
      this.rabattrechner = this.rabattrechner.bind(this);
    }

    handleChangeA(event) {
       this.setState({A: Number( event.target.value)}, this.rabattrechner);
    }
    handleChangeB(event) {

      this.setState({B: Number( event.target.value)}, this.rabattrechner);
    }

    rabattrechner(){
      console.log( this.state.A);
      console.log(  this.state.B);
      console.log( this.state.output);
      this.setState({output: this.state.A +  this.state.B});

    }

    render() {
      return (
        <div onSubmit={this.handleSubmit}>
          <label>
            Name:
            <div>
            Preis nach Reduzierung in Euro (€)
            A:<input type="number" value={this.state.A} onChange={this.handleChangeA} />Euro
              </div>
              <div>
              Höhe des Rabatts in Prozent (%)
               B:<input  nput type="number" value={this.state.B} onChange={this.handleChangeB} />%

              </div>
          </label>
        <button onClick={this.rabattrechner}>Run</button>
        <div>
        Output:
       <p>So viele Euro haben Sie gespart: {this.state.gespart}</p>

        </div>
        </div>
      );
    }
  }
  export default Rabattrechner;
